import React, { useState } from 'react';
import './App.css';

function App() {
  state = {
    id: "",
  }

  handleChange = (e) =>{
    this.setState({
      [e.target.name] : e.target.value,
    });
  }

  submitId = () =>{
    const post = {
      plzid: this.state.id,
    };

    fetch("http://localhost:8000/test", {
      method: "post",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(post),
    });
  };

  return (
    <div className="main">
      <input onChange={this.handleChange} name="id"/>
      <button onClick={this.submitId}>submit</button>
      <h1>{this.state.id} 입니다</h1>
      <hr/>
      <div className="post">
        <span className="num">번호</span>
        <span className="title">제목</span>
        <span className="comments"> [0] </span>
        <span className="author">작성자</span>
        <span className="views">조회수</span>
      </div>
      <hr/>
    </div>
  );
}

export default App;
