import React, { useState } from 'react';
import styles from './Register.module.css';
import axios from 'axios';

function Register() {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [nickname, setNickname] = useState("");
  
  // 회원가입 버튼 클릭시 서버에 요청
  const handleRegister= async () => {
    try {
      const response = await axios.post('/api/register', {
        id: id,
        password: password,
        nickname: nickname,
      });
      window.location.href = '/';
      alert("회원가입에 성공했습니다!");
    } catch (error) {
      alert("에러 발생: ", error);
    }
  };

  // 중복체크 버튼 클릭시 서버에 요청
  const handleIdCheck = async () => {
    try {
      const response = await axios.post('/api/idcheck', {
        id: id,
      });
      if(response.data.exists) {
        alert("이미 사용중인 아이디 입니다.");
      } else {
        alert("사용 가능한 아이디입니다.");
      }
    } catch (error) {
      alert("에러 발생: ", error);
    }
  }

  return (
      <div className={styles.register}>
          <div className={styles.id}>
            <input type="text" placeholder="아이디를 입력해주세요(최소 4글자)"className={styles.input_id} onChange={(e) => setId(e.target.value)}/><br/>
            <span>아이디 중복체크가 필요합니다.</span>
            <button type="button" className={styles.btn_check} onClick={handleIdCheck}>중복체크</button>
          </div>

          <div className={styles.pwd}>
            <input type="password" placeholder="비밀번호를 입력해주세요" className={styles.input_pwd1} onChange={(e) => setPassword(e.target.value)}/><br/>
            <input type="password" placeholder="비밀번호를 재입력해주세요" className={styles.input_pwd2}/>
            <span className={styles.span_pwd}></span>
          </div>

          <div className="nickname">
            <input type="text" placeholder="닉네임을 입력해주세요(2~12글자)" className={styles.input_nick} onChange={(e) => setNickname(e.target.value)}/>
            <span className={styles.span_nick}></span>
          </div>
          
          <button type="button" className={styles.btn_register} onClick={handleRegister}>회원가입</button>
      </div>
  );
}

export default Register;
