import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [posts, setPosts] = useStaet([]);

  useEffect(() => {
    async function LoadPost(){
      try {
        const response = await axios.get('/api/loadpost');
        setPosts(response.data);
      } catch(error) {
        console.error('게시글 로딩 중 오류가 발생했습니다.', error.message);
      }
    }
  }, []);

  return (
    <div className="main">
      <hr/>
      <div className="post">
        <span className="num">번호</span>
        <span className="title">제목</span>
        <span className="comments"> [0] </span>
        <span className="author">작성자</span>
        <span className="views">조회수</span>
      </div>
      <hr/>
    </div>
  );
}

export default App;
