import React from 'react';
import ('./Pagination.module.css');

function Pagination({ total, limit, page, setPage }) {
    const numPages = Math.ceil(total / limit);

    // 한번에 5개의 페이지를 표시하게 설정
    const groupNumber = Math.ceil(page / 5);

    // 첫번째 페이지번호와 마지막 페이지 번호 계산
    const firstPage = (groupNumber - 1) * 5 + 1;
    const lastPage = Math.min(groupNumber * 5, numPages);
  
    return (
      <div className='Pagination'>
        {/* 첫번째 페이지로 이동하는 << 버튼 */}
        <button onClick={() => setPage(1)} disabled={page === 1}>{"<<"}</button>
        
        {/* 이전 페이지 목록으로 이동하는 < 버튼 */}
        <button onClick={() => setPage(firstPageInGroup - 1)} disabled={groupNumber === 1}>{"<"}</button>

        {/* 페이지 번호 표시 */}
        {Array(numPages)
          .fill()
          .map((_, i) => (
            <button key={i + 1} onClick={() => setPage(i + 1)} aria-current={page === i + 1 ? "page" : undefined}>{i + 1}</button>
          ))}

        {/* 다음 페이지로 이동하는 버튼 */}
        <button onClick={() => setPage(page + 1)} disabled={page === numPages}>&gt;</button>
      </div>
    );
  }

  export default Pagination;
