import React, { useState } from 'react';
import styles from './Login.module.css';

function Login() {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin= async () => {
    
    // 정보 하나라도 입력 안돼있으면 return
    if(id=="" || password=="" || nickname==""){
      alert("정보를 전부 입력해주세요.");
      return;
    }

    // 아이디 중복체크 안돼있으면 return
    if (!idChecked) {
      idInputRef.current.focus();
      alert("아이디 중복체크가 필요합니다.");
      return;
    }

    // 비밀번호 두개가 불일치면 return
    if (!pwdChecked) {
      alert("패스워드가 일치하지 않습니다.");
      return;
    }

    // 닉네임 중복체크 안돼있으면 return
    if (!nickChecked) {
      nickInputRef.current.focus();
      alert("닉네임 중복체크가 필요합니다.");
      return;
    }

    // 조건을 다 만족하면 서버로 회원가입 요청
    try {
      const response = await axios.post('/api/register', {
        id: id,
        password: password,
        nickname: nickname,
      });

      if(response.data){
        alert("회원가입에 성공했습니다!");
        window.location.href = '/';
      } 

    } catch (error) {
      alert("에러 발생");
    }

  };

  return (
    <div className={styles.login}>
        <input type="text" className={styles.input_id} placeholder="아이디를 입력해주세요"/><br/>
        <input type="password" className={styles.input_pwd} placeholder="비밀번호를 입력해주세요"/><br/>
        <button type="submit" className={styles.submit} onClick={handleLogin}>로그인</button>
    </div>
  );
}

export default Login;
