import React, { useEffect, useState } from 'react';
import styles from './Post.module.css';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function Post() {
  const { _id } = useParams(); // /post/_id와 동일한 변수명으로 데이터를 꺼낼 수 있다.
  const [post, setPost] = useState({});

  useEffect(() => {
    const getPost = async () => {
      try {
        const response = await axios.get(`/api/post/${_id}`);
        if(response.status === 200){
          setPost(response.data);
        }
      } catch(error) {
        console.error('에러:', error.message);
      }
    };

    getPost(); // useEffect 내에서 getBoard 함수를 호출
  }, [_id]); // _id 값이 변경될 때마다 useEffect를 다시 실행

  return (
    <div className={styles.post}>
        <hr/>
        <div className={styles.info}>
          <span className={styles.title}>{post.title}</span>
          <span className={styles.date}>{post.date}</span>
          <br/><br/><br/>
          <span className={styles.author}>{post.author && post.author.nickname}</span>
          <span className={styles.views}>조회수 {post.views}</span>
        </div>

        <hr/>
        <div className={styles.content}>{post.content}</div>
        <hr/>

        <div className={styles.comments}>
          <input type="textarea" className={styles.input_comment} placeholder='댓글을 남겨보세요'></input>
          <button type="submit" className={styles.submit}>등록</button>

          {post.comments.map(comment => (
            <div className={styles.comment} key={comment._id}>
              <span className={styles.comment_author}>{comment.author}</span>
              <span className={styles.comment_date}>{comment.date}</span><br/><br/>
              <span className={styles.comment_content}>{comment.content}</span><br/><br/>
              <hr/>
            </div>
          ))}
        </div>
        <hr/>
    </div>
  );
}

export default Post;
