import React from 'react';
import styles from './PostWrite.module.css';
import axios from 'axios';

function PostWrite() {

    const handleWrite = async () => {
    
        // 아이디, 패스워드 입력 안돼있으면 return
    
        // 조건 만족하면 서버로 로그인 요청
        try {
          const response = await axios.post('/api/login', {
            id: id,
            password: password,
          });
    
          if(response.data.success){ // response에 success값이 true로 넘어오면 localStorage에 id, nickname, token 값 생성해서 로그인상태 유지
            localStorage.clear();
            localStorage.setItem('id', response.data.id);
            localStorage.setItem('nickname', response.data.nickname);
            localStorage.setItem('token', response.data.token);
            window.location.href = '/';
          } 
    
        } catch (error) {
          if (error.response.data.message) {
            alert("에러: " + error.response.data.message);
          } else {
            alert("에러 발생");
          }
        }
    
      };

  return (
    <div className={styles.postwrite}>
        <input type="text" className={styles.input_title} placeholder='제목을 입력하세요(최대 20글자)'/>
        <hr/>
        <textarea className={styles.input_content} placeholder='글 내용을 입력하세요'/><br/>
        <hr/>
        <button type="button" className={styles.btn_submit} onClick={handleWrite}>등록</button>
    </div>
  );
}

export default PostWrite;
