import React, { useState, useRef } from 'react';
import styles from './Register.module.css';
import axios from 'axios';

function Register() {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [nickname, setNickname] = useState("");
  const [idChecked, setIdChecked] = useState(false);
  const idInputRef = useRef(null);

  // 회원가입 버튼 클릭시 서버에 요청
  const handleRegister= async () => {
    //id중복체크 안돼있으면 return
    if (!idChecked) {
      idInputRef.current.focus();
      alert("아이디 중복체크가 필요합니다.");
      return;
    }

    // 정보 하나라도 입력 안돼있으면 return
    if(id=="" || password=="" || nickname==""){
      alert("정보를 전부 입력해주세요.");
      return;
    }

    try {
      const response = await axios.post('/api/register', {
        id: id,
        password: password,
        nickname: nickname,
      });
      if(response.data){
        alert("회원가입에 성공했습니다!");
        window.location.href = '/';
      } 
    } catch (error) {
      alert("에러 발생");
    }
  };

  // 중복체크 버튼 클릭시 서버에 요청
  const handleIdCheck = async () => {
    if(id.length < 4){
      alert("아이디는 최소 4글자부터입니다.");
      return;
    }
    try {
      const response = await axios.post('/api/idcheck', {
        id: id,
      });
      if(response.data.exists) {
        alert("이미 사용중인 아이디 입니다.");
      } else {
        setIdChecked(true);
        idInputRef.current.disabled = true;
        alert("사용 가능한 아이디입니다.");
      }
    } catch (error) {
      alert("에러 발생: ", error.message);
    }
  }

  return (
      <div className={styles.register}>
          <div className={styles.id}>
            <input type="text" placeholder="아이디를 입력해주세요(최소 4글자)"className={styles.input_id} onChange={(e) => setId(e.target.value)} ref={idInputRef}/>
            <button type="button" className={styles.btn_check} onClick={handleIdCheck}>중복체크</button><br/>
          </div>

          <div className={styles.pwd}>
            <input type="password" placeholder="비밀번호를 입력해주세요" className={styles.input_pwd1} onChange={(e) => setPassword(e.target.value)}/><br/>
            <input type="password" placeholder="비밀번호를 재입력해주세요" className={styles.input_pwd2}/><br/>
            <span className={styles.span_pwd}>비밀번호가 일치하지 않습니다.</span>
          </div>

          <div className="nickname">
            <input type="text" placeholder="닉네임을 입력해주세요(2~12글자)" className={styles.input_nick} onChange={(e) => setNickname(e.target.value)}/><br/>
          </div>
          
          <button type="button" className={styles.btn_register} onClick={handleRegister}>회원가입</button>
      </div>
  );
}

export default Register;
