import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [posts, setPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1); // 현재 페이지
  const [totalPages, setTotalPages] = useState(1); // 총 페이지 수

  useEffect(() => {
    async function LoadPost(){
      try {
        const response = await axios.get(`/api/loadpost?page=${currentPage}`);
        if(response.status === 200){ // true 넘어오면 게시글 배열로 저장
          setPosts(response.data);
          console.log(response.data);
        }
      } catch(error) {
        console.error('게시글 로딩 중 오류가 발생했습니다.', error.message);
      }
    }
    LoadPost(); // 게시글 페이지에 로딩
  }, [currentPage]);

  useEffect(() => {
    // 페이지 수 계산 (전체 게시글 수가 바뀔 때마다 총 페이지 수 갱신)
    async function getTotalPages() {
      try {
        const response = await axios.get('/api/totalpages');
        if (response.status === 200) {
          setTotalPages(response.data.totalPages);
        }
      } catch (error) {
        console.error('페이지 수 로딩 중 오류가 발생했습니다.', error.message);
      }
    }
    getTotalPages();
  }, [posts]); // 게시글이 로딩될 때마다 페이지 수 재계산

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const renderPagination = () => {
    const pagination = [];
    const maxVisibleButtons = 5; // 한 번에 표시될 버튼 수

    // '<<' 버튼 (첫 페이지로 이동)
    pagination.push(
      <button
        key="<<"
        onClick={() => handlePageChange(1)}
        disabled={currentPage === 1}
      >
        {'<<'}
      </button>
    );

    // '<' 버튼 (이전 페이지로 이동)
    pagination.push(
      <button
        key="<"
        onClick={() => handlePageChange(currentPage - 1)}
        disabled={currentPage === 1}
      >
        {'<'}
      </button>
    );

    // 페이지 버튼
    for (let i = Math.max(1, currentPage - Math.floor(maxVisibleButtons / 2)); i <= Math.min(totalPages, currentPage + Math.floor(maxVisibleButtons / 2)); i++) {
      pagination.push(
        <button
          key={i}
          onClick={() => handlePageChange(i)}
          className={currentPage === i ? 'active' : ''}
        >
          {i}
        </button>
      );
    }

    // '>' 버튼 (다음 페이지로 이동)
    pagination.push(
      <button
        key=">"
        onClick={() => handlePageChange(currentPage + 1)}
        disabled={currentPage === totalPages}
      >
        {'>'}
      </button>
    );

    // '>>' 버튼 (마지막 페이지로 이동)
    pagination.push(
      <button
        key=">>"
        onClick={() => handlePageChange(totalPages)}
        disabled={currentPage === totalPages}
      >
        {'>>'}
      </button>
    );

    return pagination;
  };

  return (
    <div className="main">
      <hr/>
      <div className='post'>
        <span className="num"></span>
        <span className="title">제목</span>
        <span className="comments">[댓글수]</span>
        <span className="author">작성자</span>
        <span className="views">조회수</span>
      </div>
      {posts.map(post => (
        <div className="post" key={post._id}>
          <span className="num">{post.num}</span>
          <span className="title">{post.title}</span>
          <span className="comments">[{post.comments.length}]</span>
          <span className="author">{post.author.nickname}</span>
          <span className="views">{post.views}</span>
        </div>
      ))}
      <hr/>
      <div className="pagination">
        {renderPagination()}
      </div>
    </div>
  );
}

export default App;
