import React from 'react';
import styles from './Post.module.css';

function Post() {
  return (
    <div className={styles.post}>
        <hr/>
        <div className={styles.info}>
          <span className={styles.title}>제목이길어지면어떻게될까제목이길어지면어떻게될까제목이길어지면어떻게될까</span>
          <span className={styles.date}>2024.01.01. 00:00</span>
          <br/><br/><br/>
          <span className={styles.author}>작성자</span>
          <span className={styles.views}>조회수</span>
        </div>

        <hr/>
        <div className={styles.content}>내용</div>
        <hr/>

        <div className={styles.comment}>
          <p>댓글</p>
          <input type="textarea" className={styles.comment_content} placeholder='댓글을 남겨보세요'></input>
          <button type="submit" className={styles.submit}>등록</button>
        </div>
        <hr/>
    </div>
  );
}

export default Post;
